import React from "react";
import "../../App.css";
import { Button } from "../Button/Button";
import "./HeroSection.css";

const HeroSection = ({ reference, click }) => {
  return (
    <div className="hero-container" ref={reference}>
      <video src="/videos/video-1.mp4" autoPlay loop muted />
      <h1>Ashif Fahim</h1>
      <p>A guy that likes to break things then fix them</p>
      <div className="hero-btns">
        <a href="/docs/Ashif_Fahim_Resume.pdf" download="Resume.pdf">
          Download <i className="fa fa-download" />
        </a>
        <Button
          className="btns"
          buttonStyle="btn--outline"
          buttonSize="btn--large"
          onClick={click}
        >
          See Projects
        </Button>

        <Button
          className="btns"
          buttonStyle="btn--primary"
          buttonSize="btn--large"
        >
          <a href="/docs/Ashif_Fahim_Resume.pdf" download>
            Know More
          </a>
          <i className="fa fa-download" />
        </Button>
      </div>
    </div>
  );
};

export default React.forwardRef(HeroSection);
